/*View structure for view v_bookdetail */

DROP VIEW IF EXISTS `v_bookdetail`;

DROP TABLE IF EXISTS `v_bookdetail`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_bookdetail` AS select `books`.`b_id` AS `b_id`,`books`.`b_isbn` AS `b_isbn`,`books`.`b_name` AS `b_name`,`books`.`b_author` AS `b_author`,`books`.`b_desc` AS `b_desc`,`books`.`b_pubid` AS `b_pubid`,`books`.`b_pubdate` AS `b_pubdate`,`books`.`b_price` AS `b_price`,`books`.`b_genid` AS `b_genid`,`books`.`b_stock` AS `b_stock`,`books`.`b_stockdate` AS `b_stockdate`,`books`.`b_statue` AS `b_statue`,`categories`.`cat_id` AS `cat_id`,`categories`.`cat_title` AS `cat_title`,`categories`.`cat_desc` AS `cat_desc`,`genres`.`gen_id` AS `gen_id`,`genres`.`gen_catid` AS `gen_catid`,`genres`.`gen_title` AS `gen_title`,`genres`.`gen_desc` AS `gen_desc`,`publishers`.`pub_id` AS `pub_id`,`publishers`.`pub_name` AS `pub_name` from (((`books` join `categories`) join `genres`) join `publishers`) where ((`books`.`b_genid` = `genres`.`gen_id`) and (`genres`.`gen_catid` = `categories`.`cat_id`) and (`books`.`b_pubid` = `publishers`.`pub_id`));

/*View structure for view v_monthaccount */

DROP VIEW IF EXISTS `v_monthaccount`;

DROP TABLE IF EXISTS `v_monthaccount`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_monthaccount` AS select `v_saledetail`.`sale_date` AS `sale_date`,(case when isnull(sum((`v_saledetail`.`item_price` * `v_saledetail`.`item_count`))) then 0 else sum((`v_saledetail`.`item_price` * `v_saledetail`.`item_count`)) end) AS `sum_income`,count(`v_saledetail`.`sale_id`) AS `sum_count` from `v_saledetail`;

/*View structure for view v_saledetail */

DROP VIEW IF EXISTS `v_saledetail`;

DROP TABLE IF EXISTS `v_saledetail`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_saledetail` AS select `saleitems`.`item_id` AS `item_id`,(`saleitems`.`item_price` * `saleitems`.`item_count`) AS `total`,`saleitems`.`item_price` AS `item_price`,`saleitems`.`item_saleid` AS `item_saleid`,`saleitems`.`item_count` AS `item_count`,`saleitems`.`item_bookid` AS `item_bookid`,`sales`.`sale_id` AS `sale_id`,`sales`.`sale_code` AS `sale_code`,`sales`.`sale_uid` AS `sale_uid`,`sales`.`sale_date` AS `sale_date`,`sales`.`sale_cardid` AS `sale_cardid`,`books`.`b_id` AS `b_id`,`books`.`b_isbn` AS `b_isbn`,`books`.`b_name` AS `b_name`,`books`.`b_author` AS `b_author`,`books`.`b_desc` AS `b_desc`,`books`.`b_pubid` AS `b_pubid`,`books`.`b_pubdate` AS `b_pubdate`,`books`.`b_price` AS `b_price`,`books`.`b_genid` AS `b_genid`,`books`.`b_stock` AS `b_stock`,`books`.`b_stockdate` AS `b_stockdate`,`books`.`b_statue` AS `b_statue`,`categories`.`cat_id` AS `cat_id`,`categories`.`cat_title` AS `cat_title`,`categories`.`cat_desc` AS `cat_desc`,`genres`.`gen_id` AS `gen_id`,`genres`.`gen_catid` AS `gen_catid`,`genres`.`gen_title` AS `gen_title`,`genres`.`gen_desc` AS `gen_desc`,`cards`.`card_id` AS `card_id`,`cards`.`card_name` AS `card_name`,`cards`.`card_discount` AS `card_discount` from ((((((`sales` join `saleitems`) join `users`) join `books`) join `cards`) join `categories`) join `genres`) where ((`sales`.`sale_id` = `saleitems`.`item_saleid`) and (`cards`.`card_id` = `sales`.`sale_cardid`) and (`sales`.`sale_uid` = `users`.`u_id`) and (`saleitems`.`item_bookid` = `books`.`b_id`) and (`books`.`b_genid` = `genres`.`gen_id`) and (`genres`.`gen_catid` = `categories`.`cat_id`));

/*View structure for view v_sales */

DROP VIEW IF EXISTS `v_sales`;

DROP TABLE IF EXISTS `v_sales`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_sales` AS select `sales`.`sale_id` AS `sale_id`,`sales`.`sale_code` AS `sale_code`,`sales`.`sale_uid` AS `sale_uid`,`sales`.`sale_date` AS `sale_date`,`sales`.`sale_cardid` AS `sale_cardid`,`saleitems`.`item_id` AS `item_id`,`saleitems`.`item_price` AS `item_price`,`saleitems`.`item_saleid` AS `item_saleid`,`saleitems`.`item_count` AS `item_count`,`saleitems`.`item_bookid` AS `item_bookid`,`cards`.`card_id` AS `card_id`,`cards`.`card_name` AS `card_name`,`cards`.`card_discount` AS `card_discount`,`users`.`u_id` AS `u_id`,`users`.`u_code` AS `u_code`,`users`.`u_pass` AS `u_pass`,`users`.`u_realname` AS `u_realname`,`users`.`u_roleid` AS `u_roleid`,`users`.`u_active` AS `u_active`,`books`.`b_id` AS `b_id`,`books`.`b_isbn` AS `b_isbn`,`books`.`b_name` AS `b_name`,`books`.`b_author` AS `b_author`,`books`.`b_desc` AS `b_desc`,`books`.`b_pubid` AS `b_pubid`,`books`.`b_pubdate` AS `b_pubdate`,`books`.`b_price` AS `b_price`,`books`.`b_genid` AS `b_genid`,`books`.`b_stock` AS `b_stock`,`books`.`b_stockdate` AS `b_stockdate`,`books`.`b_statue` AS `b_statue`,sum((`books`.`b_price` * `saleitems`.`item_count`)) AS `sum_price`,sum((`saleitems`.`item_price` * `saleitems`.`item_count`)) AS `sum_itemprice` from ((((`sales` join `saleitems`) join `cards`) join `users`) join `books`) where ((`sales`.`sale_id` = `saleitems`.`item_saleid`) and (`sales`.`sale_uid` = `users`.`u_id`) and (`sales`.`sale_cardid` = `cards`.`card_id`) and (`books`.`b_id` = `saleitems`.`item_bookid`)) group by `sales`.`sale_id`;

/* Procedure structure for procedure `proc_addsale` */

drop procedure if exists `proc_addsale`;

DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `proc_addsale`(
 in code varchar(30),
 in uid int,
 in cardid int

)
BEGIN

  insert into sales (sale_code,sale_uid,sale_date,sale_cardid) values
  (code,uid,now(),cardid);

select last_insert_id() ;
END$$

DELIMITER ;

/* Procedure structure for procedure `proc_addsaleitem` */

drop procedure if exists `proc_addsaleitem`;

DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `proc_addsaleitem`(
in price float,
in saleid int,
in counts int,
in bookid int
)
BEGIN

 insert into saleitems(item_price,item_saleid,item_count,item_bookid) values(price,saleid,counts,bookid);

 update books set b_stock=b_stock-counts where b_id=bookid;
END$$

DELIMITER ;

/* Procedure structure for procedure `proc_month_account` */

drop procedure if exists `proc_month_account`;

DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `proc_month_account`(
in year char(4)
)
BEGIN

 select concat(year,'-1') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-1-1') and concat(year,'-1-31')
union
select concat(year,'-2') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-2-1') and concat(year,'-2-30')
union
select concat(year,'-3') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-3-1') and concat(year,'-3-31')
union
select concat(year,'-4') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-4-1') and concat(year,'-4-31')
union
select concat(year,'-5') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-5-1') and concat(year,'-5-31')
union
select concat(year,'-6') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-6-1') and concat(year,'-6-31')
union
select concat(year,'-7') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-7-1') and concat(year,'-7-31')
union
select concat(year,'-8') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-8-1') and concat(year,'-8-31')
union
select concat(year,'-9') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-9-1') and concat(year,'-9-31')
union
select concat(year,'-10') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-10-1') and concat(year,'-10-31')
union
select concat(year,'-11') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-11-1') and concat(year,'-11-31')
union
select concat(year,'-12') as ym, case
  when sum(item_price*item_count) is null then 0
   else sum(item_price*item_count)
     end as sum_income,count(distinct sale_id) as sum_count
 from saleitems,sales where sale_id=item_saleid and sale_date between concat(year,'-12-1') and concat(year,'-12-31');

END$$

